<script lang="ts">
	import * as InputGroup from '$lib/components/ui/input-group/index.js';
	import CheckIcon from '@lucide/svelte/icons/check';
	import CopyIcon from '@lucide/svelte/icons/copy';
	import { UseClipboard } from '$lib/hooks/use-clipboard.svelte.js';
	import Label from './ui/label.svelte';
	import * as Password from '$lib/components/ui/password';

	const { user } = $props();

	const slug = (s: string) =>
		s
			.toLowerCase()
			.trim()
			.replace(/\s+/g, '-')
			.replace(/[^a-z0-9_-]/g, '');

	const baseFields = [
		{ label: 'Hostname', value: user?.host },
		{ label: 'Username', value: user?.username },
		{ label: 'Password', value: user?.password }
	];

	const fields = baseFields.map((f, i) => ({
		...f,
		id: `field-${i}-${slug(f.label)}`
	}));

	const clipboards = fields.map(() => new UseClipboard());
	const isPassword = (label: string) => label.toLowerCase() === 'password';
</script>

<svelte:head>
	<style></style>
</svelte:head>

<div class="grid w-full max-w-[230px] gap-4">
